import { Component } from '@angular/core';

@Component({
  selector: 'app-hotel-add-meal',
  templateUrl: './hotel-add-meal.component.html',
  styleUrls: ['./hotel-add-meal.component.css']
})
export class HotelAddMealComponent {

  title = 'HotelAddMeal';
  foodItems: string = '';
  deliveryType: string = 'Delivery';
  personCount: number | null = null;
  backgroundImage: string | ArrayBuffer | null = null;

  addItem(): void {
    console.log('Food Items:', this.foodItems);
    console.log('Delivery Type:', this.deliveryType);
    console.log('Person Count:', this.personCount);
    // Here you would typically send this data to a service
    alert('Item added successfully!');
    this.resetForm();
  }

  resetForm(): void {
    this.foodItems = '';
    this.deliveryType = 'Pick Deliverable';
    this.personCount = null;
  }

  onFileSelected(event: Event): void {
    const input = event.target as HTMLInputElement;
    if (input.files && input.files[0]) {
      const file = input.files[0];
      const reader = new FileReader();
      reader.onload = () => {
        this.backgroundImage = reader.result;
      };
      reader.readAsDataURL(file);
    }
  }
}
<div class="hotel-navbar">
  <app-hotel-navbar></app-hotel-navbar>
</div>

<div class="content-wrapper">
  <div class="form-container">
    <div class="instruction">
      <p>Please Separate Item with the comma(,)*</p>
    </div>

    <div class="input-container">
      <input
        type="text"
        placeholder="Enter your Food item......"
        [(ngModel)]="foodItems"
        class="food-input"
      />
      <input type="submit" value="add food" class="add-food">
    </div>

    <div class="added-food">

    </div>
    <div class="form-options">
      <div class="delivery-type">
        <span class="label">Delivery Type :</span>
        <select [(ngModel)]="deliveryType">
          <option value="Delivery" selected>Delivery</option>
          <option value="Pickup">Pickup</option>
        </select>
      </div>

      <div class="person-count">
        <span class="label">Approx person Can Eat :</span>
        <input
          type="number"
          placeholder="Number"
          [(ngModel)]="personCount"
          class="number-input"
        />
      </div>

      <button class="add-button" (click)="addItem()">
        <span class="plus-icon">+</span> Add Item
      </button>
    </div>
  </div>
</div>
