import { Component } from '@angular/core';

@Component({
  selector: 'app-user-waiting-screen',
  templateUrl: './user-waiting-screen.component.html',
  styleUrls: ['./user-waiting-screen.component.css']
})
export class UserWaitingScreenComponent {

  title = 'nourish-now';
  selectedFile: File | null = null;

  onFileSelected(event: any): void {
    this.selectedFile = event.target.files[0];
  }

  onDragOver(event: DragEvent): void {
    event.preventDefault();
    event.stopPropagation();
  }

  onDrop(event: DragEvent): void {
    event.preventDefault();
    event.stopPropagation();

    if (event.dataTransfer?.files.length) {
      this.selectedFile = event.dataTransfer.files[0];
    }
  }
}
<div class="verification-container">
  <div class="content-wrapper">
    <div class="left-section">
        <img src="./assets/VerificationImage.svg" alt="">
    </div>

    <div class="right-section">
      <h1>Welcome to Nourish Now!</h1>
      <p>
        We're reviewing your details to create a safe and impactful experience
        for all.
      </p>
      <p>
        Your journey toward reducing food waste and feeding those in need is
        just beginning.
      </p>
      <p class="thank-you">
        Thank you for being a part of something that truly matters.
      </p>
    </div>
  </div>
</div>
